import React from 'react'
import Navigation from '../Navigation/Navigation'
import './Header.css'

function Header(props) {
    const [isClicked, setIsClicked] = React.useState(false)

    function handleClick() {
        setIsClicked(true)
    }

    return (
        <header className="header">
            <h1 className="header__title">NewsExplorer</h1>
            <Navigation click={props.onClickOut} handleClick={props.onClick} loggedIn={props.loggedInHeader} userName={props.userNameHeader} />
            <div onClick={handleClick} className={isClicked ? `header__mobile header__mobile_active` : `header__mobile`}>
                <div className={isClicked ? `header__mobile-popup` : `header__mobile-popup_active`}>
                    <div className={isClicked ? `header__mobile-container header__mobile-container_active` : `header__mobile-container`}></div>
                    <div className={isClicked ? `header__mobile-box header__mobile-box_active` : `header__mobile-box`}></div>
                </div>

            </div>
        </header>
    )
}

export default Header;
